package program;

public class ConcatenateOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "123";
		String str2 = str1 + 10;
		int i =123;
		double d = 3.12;
		System.out.println(str2);
		System.out.println(i+d);
		System.out.println(i+" "+d);
		System.out.println("i = " +i + ", d =" +d);
		

	}

}
